INSERT INTO reflection_notes (user_id, content, created_at) VALUES
('00000000-0000-0000-0000-000000000000', 'scene_009の紹介で来店したお客様対応で、期待値の高い非会員への接客スキルが向上しました。紹介元への配慮も忘れずに。', NOW() - INTERVAL '2 hours'),
